package week1day1;

public class ExploreWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Sum of First 10 Numbers
		int number=1;
		int sum=0;
		while(number<=10)
		{
			sum=sum+number;
			number=number+1;
		}
		System.out.println("The sum of First 10 numbers is "+sum);
	}

}
