package week1day1;

public class ShortHandOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number=10;
		number+=number;
		System.out.println(number);
		number*=number;
		System.out.println(number);
		number/=number;
		System.out.println(number);
		number-=number;
		System.out.println(number);

	}

}
